mapper.select:
		 SELECT * 
		    FROM reserve_guest
		    WHERE user_code = (
		        SELECT user_code 
		        FROM user 
		        WHERE user_id = #{id}
    		) AND status=1
    		ORDER BY 1 DESC
			LIMIT #{offset}, #{limit}

mapper.update:
UPDATE reserve_guest
SET status = 0
WHERE reserve_guest_code = #{reserveGuestCode}

