mapper.select:
		 SELECT * 
		    FROM reserve_library
		    WHERE user_code = (
		        SELECT user_code 
		        FROM user 
		        WHERE user_id = #{id}
    		) AND status=1
    		ORDER BY 1 DESC
			LIMIT #{offset}, #{limit}

mapper.update:
UPDATE reserve_library
SET status = 0
WHERE reserve_lib_code = #{reserveLibCode}

mapper.select:
		 SELECT * 
		    FROM reserve_library
		    WHERE user_code = (
		        SELECT user_code 
		        FROM user 
		        WHERE user_id = #{id}
    		) AND status=0
    		ORDER BY 1 DESC
			LIMIT #{offset}, #{limit}
